package com.ruiz.operators;

public class BitWise {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x = 58;
		int y =13;
		
		System.out.println("x&y: "+(x&y));
		System.out.println("x|y: "+(x|y));
		System.out.println("x^y: "+(x^y));
		System.out.println("~x: "+(~x));
		System.out.println("x<<y: "+(x<<y));
		System.out.println("x>>y: "+(x>>y));
	}

}
