package com.ruiz.operators;

public class UniaryOperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int sum = +1;
		System.out.println(sum);
	
		sum--;
		System.out.println(sum);
		
		sum++;
		System.out.println(sum);
		
		sum = -sum;
		System.out.println(sum);
		
		boolean result = false;
		
		System.out.println(result);
		System.out.println(!result);

	}

}
